import React from 'react'
import { debounce } from '../utils/debounce'
import { useStateContext } from '../state'
import { TRef, TDimension } from '../state/types-defaults'

interface IProportion {
  width: TDimension
  height: TDimension
  offsetWidth: number
  offsetHeight: number
  proportion: string
  dispatch
}

interface IDimensions {
  width: TDimension
  height: TDimension
}

const getProportion: IProportion = function ({
  width,
  height,
  offsetWidth,
  offsetHeight,
  proportion,
  dispatch
}) {
  const [proportionWidth, proportionHeight] = proportion.split(':')
  let dimensions: IDimensions = { width: 0, height: 0 }

  if (width) {
    const heightValue = (offsetWidth * +proportionHeight) / +proportionWidth
    dimensions = {
      width,
      height: heightValue
    }
  } else {
    const widthValue = (offsetHeight * +proportionWidth) / +proportionHeight
    dimensions = {
      width: widthValue,
      height
    }
  }

  dispatch({
    type: 'SET_DIMENSIONS_DOM',
    dimensions
  })
}

function getDimensions(width: TDimension, height: TDimension, proportion: string, ref: TRef, dispatch) {
  const { offsetWidth, offsetHeight } = ref.current

  if (!width && !height) {
    const heightValue = offsetWidth / 2
    console.warn('Sled-Error: You need to provide either a width or a height. Falling back to "width: 100%" and proportion "2:1".')
    dispatch({
      type: 'SET_DIMENSIONS_DOM',
      dimensionsDOM: { width: '100%', height: heightValue }
    })
    return
  }

  if (width && height) {
    dispatch({
      type: 'SET_DIMENSIONS_DOM',
      dimensionsDOM: { width, height }
    })
    return
  }

  getProportion({ width, height, offsetWidth, offsetHeight, proportion, dispatch })
}

export default (width: TDimension, height: TDimension, ref: TRef) => {
  const [{ dimensionsDOM, proportion }, dispatch] = useStateContext()
  const { offsetWidth, offsetHeight } = ref.current

  React.useEffect(() => {
    getDimensions(width, height, proportion, ref, dispatch)
    function onResize() {
      getDimensions(width, height, proportion, ref, dispatch)
    }
    const dOnResize = debounce(onResize, 200)
    window.addEventListener('resize', dOnResize)
    return () => window.removeEventListener('resize', dOnResize)
  }, [width, height, proportion, ref.current])

  React.useEffect(() => {
    dispatch({
      type: 'SET_DIMENSIONS',
      dimensions: { width: offsetWidth, height: offsetHeight }
    })
  }, [dimensionsDOM.width, dimensionsDOM.height])

}
