import React from 'react';
import { useStateContext } from '../state';
export default (() => {
  const [{
    dragging
  }] = useStateContext();
  const [cursor, setCursor] = React.useState('auto');
  React.useEffect(() => {
    setCursor(dragging ? 'grab' : 'auto');
  }, [dragging]);
  return cursor;
});
