import { useState, useEffect } from 'react';
import { useStateContext } from '../../state';
export const useDirectionDisabled = select => {
  const [{
    rewind,
    currentIndex,
    viewCount
  }] = useStateContext();
  const {
    0: disabled,
    1: setDisabled
  } = useState(false);
  useEffect(() => {
    if (typeof select === 'string') {
      if (rewind) {
        setDisabled(false);
        return;
      }

      if (select === 'next' && currentIndex === viewCount - 1) {
        setDisabled(true);
      } else if (select === 'prev' && currentIndex === 0) {
        setDisabled(true);
      } else {
        setDisabled(false);
      }
    }
  }, [currentIndex, rewind]);
  return disabled;
};
