import React from 'react'
import { useStateContext } from '../state'

export default (proportion?: string): void => {
  const [, dispatch] = useStateContext()
  React.useEffect(() => {
    dispatch({ type: 'SET_PROPORTION', proportion })
  }, [proportion])
}
