import { useStateContext } from '../../state';

const onClickFunction = select => {
  const [{
    stopOnInteraction
  }, dispatch] = useStateContext();

  function onClick() {
    if (stopOnInteraction) {
      dispatch({
        type: 'SET_AUTOPLAY',
        autoPlayInterval: undefined
      });
    }

    if (typeof select === 'number') {
      dispatch({
        type: 'SELECT',
        index: select
      });
    } else {
      dispatch({
        type: select === 'next' ? 'NEXT' : 'PREV'
      });
    }
  }

  return onClick;
};

export default onClickFunction;
