import React from 'react'

export default (width: TDimension, height: TDimension, ref: TRef) => {
  const [{ dimensionsDOM, proportion }, dispatch] = useStateContext()

  React.useEffect(() => {
    const { offsetWidth, offsetHeight } = ref.current

    if (!width && !height) {
      dispatch({
        type: 'SET_DIMENSIONS_DOM',
        dimensionsDOM: { width: '100%' }
      })
      dispatch({ type: 'SET_PROPORTION', proportion: '2:1' })
      return
    }

    dispatch({
      type: 'SET_DIMENSIONS_DOM',
      dimensionsDOM: { width, height }
    })

  }, [width, height])

}
